import { useRef } from 'react';
import './MealItemForm.css';

const MealItemForm = (props) => {
    const amountInputRef = useRef();


    let inputClass = "form-control";
    const amountHandler = () => {
        if (+amountInputRef.current.value > 10) {
            inputClass = "form-control bg-danger-subtle"
        } else {
            inputClass = "form-control"
        }
    };

    const submitHandler = (e) => {
        e.preventDefault();
        const enteredAmount = +amountInputRef.current.value;
        if (enteredAmount <= 0 ||
            enteredAmount === '' ||
            enteredAmount > 5) {
            return;
        }
        props.onAddToCart(enteredAmount);

    }
    return (<>
        <form onSubmit={submitHandler} className="d-flex justify-content-between gap-1 align-items-end">
            <label htmlFor="input" className='fs-5 '>Amount: </label>
            <input ref={amountInputRef} onChange={amountHandler} className={inputClass} type="text" id="input" min="1" max="5" step="1" defaultValue="1" />
            <button className="btn addBtn">+Add</button>
        </form>

    </>)
};

export default MealItemForm;
