import { useState } from 'react';
import classes from './Cart.module.css';

const CartUserForm = (props) => {
    const [enteredName, setEnteredName] = useState('');
    const [nameInputTouched, setNameInputTouched] = useState(false);

    const [enteredNumber, setEnteredNumber] = useState('');
    const [numberInputTouched, setNumberInputTouched] = useState(false);

    const [enteredCity, setEnteredCity] = useState('');
    const [cityInputTouched, setCityInputTouched] = useState(false);

    const [formIsValid, setFormIsValid] = useState(false);

    const nameClasses = !enteredName && nameInputTouched ? `${classes.invalid} form-control animate__animated animate__headShake` : "form-control";
    const nameValidClass = enteredName.length > 2 && nameInputTouched && `${classes.valid}`;

    const numberClasses = !enteredNumber && numberInputTouched ? `${classes.invalid} form-control animate__animated animate__headShake` : "form-control ";
    const numberValidClass = enteredNumber.length === 10 && numberInputTouched && classes.valid;

    const cityClasses = !enteredCity && cityInputTouched ? `${classes.invalid} form-control animate__animated animate__headShake` : "form-control";
    const cityValidClass = enteredCity.length > 2 && cityInputTouched && classes.valid;

    const btnClasses = !formIsValid ? `${classes.btnInvalid} btn btn-outline-secondary` : 'btn btn-success';

    const nameInputHandler = (e) => {
        setNameInputTouched(true);
        setEnteredName(e.target.value);
        if (e.target.value.length > 2
            && enteredNumber.length === 10
            && enteredCity.length > 2) {
            setFormIsValid(true);
        } else {
            setFormIsValid(false);
        }
    };
    const numberInputHandler = (e) => {
        setNumberInputTouched(true);
        setEnteredNumber(e.target.value);
        if (enteredName.length > 2
            && e.target.value.length === 10
            && enteredCity.length > 2) {
            setFormIsValid(true);
        } else {
            setFormIsValid(false);
        }
    };
    const cityInputHandler = (e) => {
        setCityInputTouched(true);
        setEnteredCity(e.target.value);
        if (e.target.value.length > 2 && enteredName.length > 2 && enteredNumber.length === 10) {
            setFormIsValid(true);
        } else {
            setFormIsValid(false);
        }
    };

    const formSubmitHandler = (e) => {
        e.preventDefault();
        if (formIsValid) {

        }
    }


    return (<>
        <div className={`${classes.userForm} mt-3`}>
            <form className="me-3" onSubmit={formSubmitHandler}>

                <input onChange={nameInputHandler}
                    type="text" className={`${nameClasses} ${nameValidClass} mb-1`} id="name" placeholder="name"></input>
                <input onChange={numberInputHandler} type="number" inputMode="numeric" pattern="[0-9]+ " className={`${numberClasses} ${numberValidClass} mb-1`} id="Number" placeholder="Number"></input>
                <input onChange={cityInputHandler} type="city" className={`${cityClasses} ${cityValidClass} mb-1`} id="city" placeholder="city"></input>

                <div className="btn-group my-1" role="group" aria-label="Basic mixed styles example">
                    <button type="button" id="liveToastBtn" className="btn text-dark bg-danger border-danger" onClick={props.onCancel}>Cancel</button>
                    <button className={btnClasses} disabled={!formIsValid} >
                        Place Order
                    </button>
                </div>

                <div className="toast-container position-fixed bottom-0 end-0 p-3">
                    <div id="liveToastBtn" className="toast" role="alert" aria-live="assertive" aria-atomic="true">
                        <div className="toast-header">
                            <img src="..." className="rounded me-2" alt="..." />
                            <strong className="me-auto">Bootstrap</strong>
                            <small>11 mins ago</small>
                            <button type="button" className="btn-close" data-bs-dismiss="toast" aria-label="Close"></button>
                        </div>
                        <div className="toast-body">
                            Hello, world! This is a toast message.
                        </div>
                    </div>
                </div>
            </form>
        </div>
    </>);
}
export default CartUserForm;
